import { TOTAL_SCREENS } from "./CommonUtils";
import { Subject } from 'rxjs';
export default class ScrollService {
  constructor() {
    this.scrollToHireMe = () => {
      let contactMeScreen = document.getElementById("ContactMe");
      if (!contactMeScreen) return;
      contactMeScreen.scrollIntoView({
        behavior: "smooth"
      });
    };

    this.scrollToHome = () => {
      let homeScreen = document.getElementById("Home");
      if (!homeScreen) return;
      homeScreen.scrollIntoView({
        behavior: "smooth"
      });
    };

    this.isElementInView = (elem, type) => {
      let rec = elem.getBoundingClientRect();
      let elementTop = rec.top;
      let elemBottom = rec.bottom;
      let partiallyVisible = elementTop < window.innerHeight && elemBottom >= 0;
      let completelyVisible = elementTop >= 0 && elemBottom <= window.innerHeight;

      switch (type) {
        case "partial":
          return partiallyVisible;

        case "complete":
          return completelyVisible;

        default:
          return false;
      }
    };

    this.checkCurrentScreenUnderViewport = event => {
      if (!event || Object.keys(event).length < 1) return;

      for (let screen of TOTAL_SCREENS) {
        let screenFromDOM = document.getElementById(screen.screen_name);
        if (!screenFromDOM) continue;
        let fullyVisible = this.isElementInView(screenFromDom, "complete");
        let partiallyVisible = this.isElementInView(screenFromDom, "partial");

        if (fullyVisible || partiallyVisible) {
          if (partiallyVisible && !screen.alreadyRendered) {
            ScrollService.currentScreenFadeIn.next({
              fadeInScreen: screen.screen_name
            });
            screen['alreadyRendered'] = true;
            break;
          }

          if (fullyVisible) {
            ScrollService.currentScreenBroadCaster.next({
              screenInView: screen.screen_name
            });
            break;
          }
        }
      }
    };

    /* ADD SCROLL EVENT TO WINDOW */
    window.addEventListener("scroll", this.checkCurrentScreenUnderViewport);
  }

}
