import React, { useState } from "react";
import ScreenHeading from "../../Utilities/ScreenHeading/ScreenHeading";
import ScrollService from "../../Utilities/ScrollService";
import Animations from "../../Utilities/Animations";
import './Resume.css';
export default function Resume(props) {
  const [selectedBulletIndex, setSelectedBulletIndex] = useState(0);
    const [carousalOffSetStyle, setCarousalOffSetStyle] = useState({});

    let fadeInScreenHandler = (screen) => {
    if (screen.fadeScreen !== props.id) return;
    Animations.animations.fadeInScreen(props.id);
  };
  const fadeInSubscription =
    ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

    const ResumeHeading = (props) => {
        return (
           <div className="resume-heading">
      <div className="resume-main-heading">
        <div className="heading-bullet">
          <span>{props.heading ? props.heading : ""}</span>
          {props.fromDate && props.toDate ? (
            <dov className="heading-date">
              {props.fromDate + "_" + props.toDate}
            </dov>
          ) : (
            <div></div>
          )}
        </div>
        <div className="resume-sub-heading">
          <span>{props.subHeading ? props.subHeading : ""}</span>
        </div>
        <div className="resume-heading-description">
          <span>{props.description ? props.description : ""}</span>
        </div>
      </div>
    </div>
      )
   
  };

  const resumeBullets = [
    { label: "Education", logoSrc: "education.svg" },
    { label: "Work History", logoSrc: "work-history.svg" },
    { label: "Programming skills", logoSrc: "programming-skills.svg" },
    { label: "Projects", logoSrc: "projects.svg" },
    { label: "Interests", logoSrc: "interests.svg" },
  ];

  const programmingSkillDetails = [
    { skill: "JavaScript", ratingPercentage: 50 },
    { skill: "PHP", ratingPercentage: 80 },
    { skill: "Java", ratingPercentage: 50 },
    { skill: "C++", ratingPercentage: 50 },
    { skill: "Python", ratingPercentage: 50 },
    { skill: "ReactJs", ratingPercentage: 50 },
    { skill: "SQL", ratingPercentage: 50 },
    { skill: "HTML", ratingPercentage: 50 },
    { skill: "CSS", ratingPercentage: 50 },
    { skill: "BootStrap", ratingPercentage: 50 },
    { skill: "JEE", ratingPercentage: 50 },
  ];

  const projectDetails = [
    {
      title: "Personal Portfolio Website",
      duration: { fromDate: "2022", toDate: "2022" },
      description:
        "A personal Portfolio website to show all my details and projects at one place",
      subHeading: "Technologies Used: React JS, Bootsrap",
    },
    {
      title: "Mobile E-shop",
      duration: { fromDate: "2020", toDate: "2021" },
      description:
        "Technologies Used: React Native, Mongo DB, Express Js, Node Js, Redux.",
      subHeading:
        "An ecommerce application designed to sell products online wth payment system integration",
    },
    {
      title: "Ecommerce Website",
      duration: { fromDate: "2020", toDate: "2021" },
      description:
        "Technologies Used: Mongo DB, Epress Js, React Js, Node JS, Redux, Bootstrap.",
      subHeading:
        "Online ecommerce website for showcasing and selling products onlne with payment system integration, both Paypal and Stripe",
    },
  ];

  const resumeDetails = [
    <div classNmae="resume-screen-container" key="education">
      <ResumeHeading
        heading={"University of Legal, Economic and Social Sciences, Morocco"}
        subHeading={
          "Bachelor degree in computer science methods applied to business management"
        }
        fromDate={"2021"}
        toDate={"2022"}
      />
      <ResumeHeading
        heading={"Higher School of Technology, Morocco"}
        subHeading={"diploma in computer engineering"}
        fromDate={"2018"}
        toDate={"2021"}
      />
      <ResumeHeading
        heading={"High School"}
        subHeading={"Hassan 2 in Morocco"}
        fromDate={"2016"}
        toDate={"2018"}
      />
    </div>,
    <div classNmae="resume-screen-container" key="work-experience">
      <ResumeHeading
        heading={"OCP group intern, Morocco"}
        subHeading={"End of study intern, worked on a website project for managing employees"}
        fromDate={"2018"}
        toDate={"2018"}
        />
        <div className="experience-description">
            <span className="resume-description-text">
                Currently working as MERN stack web and mobile developer and also an online instructor on udemy
            </span>
        </div>
        <div className="experience-desciption">
            <span className="resume-description-text">
                - Developed an ecommerce website for client with the dashboard for managing the products,
                managing reviews, users, payment etc. .
            </span>
            <br />
            <span className="resume-description-text">
                Integrated the web app with backend services to create new
                user onboarding application with dynamic form content.
            </span>
            <br />
            <span className="resume-description-text"> I stretch my mental capacity to develope UI as per the given designs.
            </span>
        </div>,
       
        <div className="reume-screen-container programming-skills-container" key="programming-skills">
            {programmingSkillDetails.map((skill, index) => (
                <div className="skill-parent" key={index}>
                    <div className="heading-bullet"></div>
                    <span>{skill.skill}</span>
                    <div className="skill-percentage">
                        <div style={{ width: skill.ratingPercentage + "%" }}
                            className="active-percentage">
                            

                        </div>
                    </div>
                </div>
            ))}
        </div>,

        <div className="resume-screen-container" key="projects">
            {projectDetails.map((projectDetails, index)=>(
                <ResumeHeading
                    key={index}
                    heading={projectDetails.title}
                    subHeading={projectDetails.subHeading}
                    description={projectDetails.description}
                    fromDate={projectDetails.duration.fromDate}
                    toDate = {projectDetails.duration.toDate}
                />
            ))
            }
        </div>,

        <div className="resume-screen-container" key="interest">
            <ResumeHeading
                heading='Teaching'
                description='Apart from being a tech enthusiast and a code writer, i also love to teach people what i know simply because i believe in sharing.'

            />
              <ResumeHeading
                heading='Music'
                description="Listening to soothing music is something i can never compromise with, skimming through Spotify's pop songs charts is at times the best stress reliever that i can get my hands on."
                
            />
              <ResumeHeading
                heading='Competitive Gaming'
                description='I like to challenge my reflexes a lot while competing in football games, pushing the rank and having interactive gaming sessions excites me the most.'
                
            />
           </div>

    </div>
  ];

    const handleCarousal = (index) => {
        let offsetHeight = 360;
        let newCarousalOffset = {
            style: { transform: "translateY(" + index * offsetHeight * -1 + "px )" },
        };

        setCarousalOffSetStyle(newCarousalOffset);
        setSelectedBulletIndex(index);
    };

    const getBullet = () => {
        return resumeBullets.map((bullet, index) =>
            <div
            
                onClick={() => handleCarousal(index)}
                className={index === selectedBulletIndex ? "bullet selected-bullet" : "bullet"}
                key={index}
            >
                <img className="bullet-logo"
                    src={require(`../../assets/Resume/${bullet.logoSrc}`)}
                    alt='oops ,, no internet'
                />
                <span className="bullet-label">{bullet.label}</span>
            </div>
        );
    };
   
    const getResumeScreens = () => {
        /*hna howa 3iwad resumeDetails li west map dayr hadi resumeDetail o 7ta li mn wra map*/
        return (
            <div
                style={carousalOffSetStyle.style}
                className='resume-details-carousal'

            >
                {resumeDetails.map((resumeDetails)=>resumeDetails)} 
            </div>
       )
   }

  return (
    <div className="resume-container screen-container"  id={props.id || ""}>
      <div className="resume-content">
              <ScreenHeading title={"Resume"} subHeading={"My Formal Bio Details"} />
              <div className="resume-card">
                  <div className="resume-bullets">
                      <div className="bullet-container">
                          <div className="bullet-icons"></div>
                          <div className="bullets">{ getBullet()}</div>
                      </div>
                  </div>
                  <div className="resume-bullet-details">{ getResumeScreens()}</div>
              </div>
      </div>
    </div>
  );
}
