import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import Typical from 'react-typical';
import imgBack from '../../../src/images/mailz.jpeg';
import load1 from '../../../src/images/load2.gif';
import ScreenHeading from '../../Utilities/ScreenHeading/ScreenHeading';
import ScrollService from '../../Utilities/ScrollService';
import Animations from '../../Utilities/Animations';
import './ContactMe.css'
export default function Contactme(props) {
        let fadeInScreenHandler = (screen) => {
        if (screen.fadeInScreen !== props.id)
            return
        Animations.animations.fadeInScreen(props.id)
    }
    const fadeInSubscription = ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [banner, setBanner] = useState("");
    const [bool, setBool] = useState(false);
    
    const handleName = (e) => {
        setName(e.target.value);
    };
   
    const handleEmail = (e) => {
        setEmail(e.target.value);
    };
       

    const handleMessage = (e) => {
        setMessage(e.target.value);
    };
    
    const submitForm =async (e) => { 
      e.preventDefault();
      try {
           let data = {
        name,
        email,
        message,
      }
        setBool(true);
        const res = await axios.post(`/contact`, data);
        if (name.length === 0 || email.length === 0 || message.length === 0) {
          setBanner(res.data.msg);
          toast.error(res.data.msg);
          setBool = (false);
        } else if (res.status === 200) {
           setBanner(res.data.msg);
          toast.success(res.data.msg);
          setBool = (false);
        }
      } catch (error) {
        console.log(error)
      }
   
  };
  return (
      <div className='main-container fade-in' id={props.id || ''}>
          <ScreenHeading
              subHeading={"Let's Keep In Touch"}
              title= {"Contact Me"}
          />
          <div className='central-form'>
              <div className='col'>
                  <h2 className='title'>
                {""}
                <Typical
                  loop={Infinity}
                  steps={[
                    "Get in Touch ðŸ“§",
                    1000,
                  ]}
                />
                  </h2>
                   <a href="https://web.facebook.com/demoisell.asmae/">
              <i className="fa fa-facebook-square"></i>
            </a>
            <a href="#">
              <i className="fa fa-google-plus-square"></i>
            </a>
            <a href="https://www.instagram.com/bellissad_asmae/">
              <i className="fa fa-instagram"></i>
            </a>
            <a href="https://www.youtube.com/channel/UCQW_cRp29PHBfz_7kld5FNA">
              <i className="fa fa-youtube-square"></i>
            </a>
            <a href="https://twitter.com/Asmae51483526">
              <i className="fa fa-twitter"></i>
            </a>
              </div>
              <div className='back-form'>
                  <div className='img-back'>
                      <h4>Send Your Email Here</h4>
                        <img src={imgBack} alt='image not found'/>
                  </div>
                  <form onSubmit={submitForm}>
                      <p>{banner}</p>
                      <label htmlFor='name'>Name</label>
                      <input type="text"
                          onChange={handleName}
                          value={name}
                      />
                      <label htmlFor='email'>Email</label>
                      <input type="email"
                      onChange={handleEmail}
                          value={email}
                      />
                      <label htmlFor='message'>Message</label>
                      <textarea type="text"
                      onChange={handleMessage}
                          value={message}
                      />
                      <div className='send-btn'>
                          <button type='submit'>
                send<i className='fa fa-paper-plane' />
                {bool ? (<b className='load'>
                  <img src={load1} alt='image not responding' /></b>)
                 : ("")}
                          </button>
                      </div>
          </form>
          
        </div>
        <button onClick={() => ScrollService.scrollHandler.scrollToHome()} ></button>
          </div>
    </div>
  )
}
